import JuiceEditorKit
import OSLog
import SwiftUI
import MagicKit

@main
struct JuiceEditorTestApp: App, SuperLog {
    static let emoji = "🍎"
    
    @State var editorView: EditorView?

    var body: some Scene {
        WindowGroup {
            if let e = editorView {
                e
            } else {
                ProgressView().onAppear {
                    self.editorView = EditorView(delegate: self, verbose: true)
                }
            }
        }
    }
}

extension JuiceEditorTestApp: EditorDelegate {
    func getHtml(_ uuid: String) -> String? {
        """
        <h1>Hi from JuiceEditorTestApp</h1>
        <p>hi</p>
        <p>你好</p>
        <h2>画图</h2>
        <img src="data:image/png;base64," draw="true"/>
        """
    }

    func onReady() {
        os_log("\(t)OnReady")
        Task {
            try? await self.editorView?.setContent("1")
        }
    }
    
    func onUpdateDoc(_ html: String) {
        os_log("\(t)OnUpdateDoc: \(html)")
    }
}
